    const titleTransform = groupMetadata.titleTransform || ((title: string) => title);
    return this.contextProvider.pipe(
      map((context) => {
        groups.forEach((g) => {
          g.title = titleTransform(g.title, context);
        });
        return groups;
      })
    );
  }

  private getGroupMetadata(group: string): GrouperMetadata<T, C> {
