import { Injectable } from '@angular/core';
// import { ActivityDefinitionService } from '@core/definition-services/activity-definition.service';
import { DefinitionService } from '@core/definition-services/definition.service';
import { ClansMembersService } from '@core/services/clans-members.service';
import { SeasonService } from '@core/services/season.service';
import {
  selectAllClansMembersProfiles,
  selectClanMemberProfileStateLoading
} from '@core/store/clans-members-profiles/clan-members-profiles.selectors';
import { getClanMemberId, getMemberProfileId } from '@destiny/data/utility';
import { Store } from '@ngrx/store';
import { ClanMemberProfile } from '@shared/models/ClanMemberProfile';
import { ClanProfileService } from 'projects/data/src/lib/clan/profiles/profile.service';
import { from, map, mergeMap, Observable, switchMap, take, toArray } from 'rxjs';
import { ActivitiesShellModule } from '../activities-shell/activities-shell.module';
import {
  CuratedActivityGroupDefinitions,
  CURATED_ACTIVITIES_ALL,
  CURATED_ACTIVITY_GROUPS
} from '../models/CuratedActivities';

@Injectable({
  providedIn: ActivitiesShellModule
})
export class ActivitiesService {
  constructor(
    //private activityDefinitionService: ActivityDefinitionService,
    private definitionService: DefinitionService,
    private store: Store,
    private memberService: ClansMembersService,
    private profileService: ClanProfileService,
    private seasonService: SeasonService
  ) {}

  getCuratedActivities(): CuratedActivityGroupDefinitions[] {
    const groups = CURATED_ACTIVITY_GROUPS;

    return groups.map((group) => {
      return {
        title: group.title,
        activities: group.activities.map((ca) => this.definitionService.activityDefinition[ca.hash])
      };
    });
  }

  getActivityById(hash: number) {
    return this.definitionService.activityDefinition[hash];
  }

  getCuratedMetrics(hash: number): any[] {
    const curatedMetrics = CURATED_ACTIVITIES_ALL.find((ca) => ca.hash === hash).metrics || [];

    return curatedMetrics.map((cm) => this.definitionService.metricDefinitions[cm]);
  }

  getCuratedCollections(hash: number): any[] {
    const curatedCollections = CURATED_ACTIVITIES_ALL.find((ca) => ca.hash === hash).collections || [];
    return curatedCollections.map((cm) => this.definitionService.collectibleDefinition[cm]);
  }
  getCuratedRecords(hash: number): any[] {
    const curatedRecords = CURATED_ACTIVITIES_ALL.find((ca) => ca.hash === hash).records || [];
    return curatedRecords.map((cm) => this.definitionService.recordDefinition[cm]);
  }

  clanProfiles$: Observable<ClanMemberProfile[]> = this.store.select(selectAllClansMembersProfiles); //.pipe(
  clanProfilesLoading$: Observable<boolean> = this.store.select(selectClanMemberProfileStateLoading); //

  getProfiles(
    metricHashes: number[],
    collectionHashes: number[],
    recordHashes: number[]
  ): Observable<ClanMemberProfile[]> {
    return this.memberService.clanMembers$.pipe(
      switchMap((clansAndMembers) => {
        return from(clansAndMembers).pipe(
          mergeMap((clanAndMembers) => {
            return this.profileService
              .getSerializedProfilesFromCache(
                clanAndMembers.clan.clanId,
                clanAndMembers.members,
                this.seasonService.getSeasonProgressionHashes(),
                collectionHashes,
                recordHashes,
                metricHashes
              )
              .pipe(
                switchMap((memberProfiles) => {
                  return clanAndMembers.members.map((member) => {
                    return {
                      member,
                      profile: memberProfiles.find((m) => getMemberProfileId(m) === getClanMemberId(member)),
                      clan: {
                        clanId: clanAndMembers.clan.clanId,
                        clanName: clanAndMembers.clan.clanName,
                        clanTag: clanAndMembers.clan.clanTag
                      }
                    };
                  });
                })
              );
          }),
          toArray(),
          map((x) => x.flatMap((y) => y))
        );
      })
    );
  }
}

import { ActivityModeShellModule } from '../activity-mode-shell/activity-mode-shell.module';
import { DESTINY_ACTIVITY_MODES } from '@destiny/data/models';
import { DefinitionService } from '@core/definition-services/definition.service';
import { Injectable } from '@angular/core';
import { ClansMembersService } from '@core/services/clans-members.service';
import { of, switchMap } from 'rxjs';
import { ProfileRecentActivityWorkerService } from '../../../workers/profile-recent-activity/profile-recent-activity.service';
import { DestinyDefinitionsDestinyActivityModeDefinition } from 'bungie-api-angular';
import { TrackedDuration } from 'projects/data/src/lib/clan/clan-member-recent-activity/clan-member-recent-activity.serializer';
@Injectable({
  providedIn: ActivityModeShellModule
})
export class ActivityModeService {
  constructor(
    private definitionService: DefinitionService,
    private clansDetailsService: ClansMembersService,
    private profileRecentActivityWorkerService: ProfileRecentActivityWorkerService
  ) {}
  readonly ignoredModes = [
    16, // NFStrikes - no stats
    66, // Forges
    17, // Prestiege NF
    58, // Heroic adv
    47, // Scored Pres NF
    77, // Menagerie
    78, // Vex offensive
    83, // SUndial
    76, // Reckoning
    72, // Clash comp
    74, // Control comp,
    67, // Salvage
    42, // Trials 9 survival
    15, // crimson doubles
    39, // Trials of nine
    38, // Countdown
    49, // Doubles
    41, // Trials 9 count
    50, // Doubles
    31, // Supremecy.
    68, // IB Salvage
    43, // IB Control
    44, // IB control
    45, // IB Supremacy
    90, // IB Rift
    61, // Scorched
    60, // Lockdown
    65, // Break through
    80, // Elimination
    89, // Zone control
    75, // GB Prime
  ];

  getActivityModeDefinition(modeType: number) {
    const x = Object.keys(this.definitionService.activityModeDefinition).map(
      (x) => this.definitionService.activityModeDefinition[x]
    ) as DestinyDefinitionsDestinyActivityModeDefinition[];
    return x.find((y) => y.modeType === modeType);
  }

  getGroupedActivityModes() {
    const groups = DESTINY_ACTIVITY_MODES;
    const definitions = Object.keys(this.definitionService.activityModeDefinition).map(
      (x) => this.definitionService.activityModeDefinition[x]
    );
    return groups.map((x) => {
      return {
        group: x,
        activities: definitions
          .filter(
            (mode) =>
              mode.activityModeCategory === x.id &&
              mode.redacted === false &&
              mode.blacklisted === false &&
              mode.display === true &&
              this.ignoredModes.indexOf(mode.modeType) === -1
          )
          .sort((a, b) => a.order - b.order)
      };
    });
  }

  getAllActivitiesByMode(mode: number, trackedDates: TrackedDuration[]) {
    return this.clansDetailsService.clanMembersProfiles$.pipe(
      switchMap((clanMembersProfiles) => {
        return this.profileRecentActivityWorkerService.getAllRecentClanActivitiesByActivityModeId(
          clanMembersProfiles,
          trackedDates,
          mode
        );
        return of(clanMembersProfiles);
      })
    );
  }
}

import { Injectable } from '@angular/core';
import { AppConfig } from '@core/config/app-config';
import { updateBungieInfoSync, updateClanProfileSync } from '@core/store/clans';
import { addNotification, removeNotification, updateNotification } from '@core/store/notifications';
import { Store } from '@ngrx/store';
import { ClanDatabase } from 'projects/data/src/lib/clan/clan-database';
import { nowPlusMinutes } from 'projects/data/src/lib/utility/date-utils';
import { filter, from, map, mergeMap, Observable, of, take, toArray } from 'rxjs';

import { BungieInfoWorkerService } from '../../../workers/bungie-info/bungie-info.service';
import { ClanConfigMembers } from './clan-updater.service';

@Injectable({
  providedIn: 'root'
})
export class BungieInfoUpdaterService {
  infoService: BungieInfoWorkerService;
  constructor(private store: Store, private bungieInfoWorker: BungieInfoWorkerService, private appConfig: AppConfig) {
    // const clanDB = new ClanDatabase();
    // this.infoService = new BungieInfoWorkerService(clanDB, appConfig.apiKey);
  }

  updateAllClansBungieInfo(clans: ClanConfigMembers[]): Observable<ClanConfigMembers[]> {
    return from(clans).pipe(
      // TODO Verify Merge Map is appropriate for this.
      mergeMap((x) => {
        return this.updateBungieInfo(x).pipe(map((cm) => cm));
      }, 1),
      toArray()
    );
  }

  updateBungieInfo(clan: ClanConfigMembers): Observable<ClanConfigMembers> {
    const lastUpdate = new Date(clan.clanConfig.bungieInfoUpdate || '1/1/1900');
    const staleDate = nowPlusMinutes(-this.appConfig.constants.PROFILE_UPDATING_EXP_MINUTES);

    if (staleDate > lastUpdate) {
      this.store.dispatch(
        addNotification({
          notification: {
            id: 'bungieInfo',
            title: `Updating ${clan.clanConfig.clanName} Bungie Info`,
            data: { progress: 0, complete: 0, total: clan.members.length }
          }
        })
      );
      const progress = (progressCount) => {
        this.store.dispatch(
          updateNotification({
            notification: {
              id: 'bungieInfo',
              title: `Updating ${clan.clanConfig.clanName} Bungie Info`,
              data: {
                progress: progressCount / clan.members.length,
                complete: progressCount,
                total: clan.members.length
              }
            }
          })
        );
      };
      return this.bungieInfoWorker.updateAllBungieInfoCache(clan.clanConfig.clanId, clan.members, progress).pipe(
        take(1),
        map((x) => {
          // eslint-disable-next-line @ngrx/avoid-dispatching-multiple-actions-sequentially
          this.store.dispatch(
            removeNotification({
              notification: {
                id: 'bungieInfo',
                title: `Updating ${clan.clanConfig.clanName} Bungie Info`,
                data: { progress: clan.members.length, complete: clan.members.length, total: clan.members.length }
              }
            })
          );

          // eslint-disable-next-line @ngrx/avoid-dispatching-multiple-actions-sequentially
          this.store.dispatch(updateBungieInfoSync({ clanId: clan.clanConfig.clanId }));

          //
          // console.log('------------- dispatch update ------------------');
          // this.clanMemberService.forceReload();
          return {
            ...clan,
          };
        })
      );
    }

    return of(clan);
    // return this.profileService.getSerializedProfilesFromCache(clan.clanConfig.clanId, clan.members, [], [], []).pipe(
    //   map((x) => {
    //     return { ...clan, profiles: x };
    //   })
    // );
  }
}

import { Injectable } from '@angular/core';
import {  Observable, of, Subject } from 'rxjs';
import { environment } from '../../../environments/environment';

@Injectable({
  providedIn: 'root'
})
export class BungieInfoWorkerService {
  constructor() {}
  updateAllBungieInfoCache(clanId: string, clanMembers: any[], progress?: (done) => any): Observable<boolean> {
    const infoCacheComplete: Subject<boolean> = new Subject();
    const worker = new Worker(new URL('./bungie-info-updater.worker', import.meta.url));
    worker.onmessage = ({ data }) => {
      if (data.type === 'progress') {
        progress(data.data);
      } else if (data.type === 'complete') {
        infoCacheComplete.next(true);
      }
    };

    worker.postMessage({
      clanId,
      clanMembers,
      apiKey: environment.apiKey
    });
    return infoCacheComplete;
  }

  getAllActivitiesFromCache(clanId: string, memberProfiles: any[]): Observable<any> {
    const info = new Subject();
    const worker = new Worker(new URL('./bungie-info-getter.worker', import.meta.url));
    worker.onmessage = ({ data }) => {
      info.next(data.data);
    };

    worker.postMessage({
      clanId,
      memberProfiles,
      apiKey: environment.apiKey
    });
    return info;
  }
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Store } from '@ngrx/store';
import { GroupsV2GroupV2Card, GroupV2Service } from 'bungie-api-angular';
import { catchError, map, of } from 'rxjs';

import { ClanDatabase } from 'projects/data/src/lib/clan/clan-database';
import { addClan, removeClan, resetClan } from '@core/store/clans/clans.actions';

@Injectable({
  //Note can't figure out how to provide in the module I want.
  providedIn: 'root'
})
export class ClanDetailService {
  constructor(
    private groupService: GroupV2Service,
    private httpClient: HttpClient,
    private store: Store,
    private db: ClanDatabase
  ) {}

  getClan(clanId) {
    return this.groupService.groupV2GetGroup(clanId).pipe(
      map((clanResult) => {
        return clanResult.Response.detail;
      }),
      catchError((error) => {
        if (error.error.ErrorStatus === 'ClanNotFound') {
          return of({
            groupId: clanId,
            name: `Clan Not Found - ${clanId}`,
            clanInfo: { clanCallsign: '' }
          });
        }

        throw Error(`Clan with id ${clanId} returned error`);
      })
    );
  }

  removeClan(clanId: string) {
    this.store.dispatch(removeClan({ clanId }));
    this.db.purgeDatabase(clanId);
  }

  resetClan(clan: GroupsV2GroupV2Card) {
    this.db.purgeDatabase(clan.groupId.toString());
    this.store.dispatch(
      resetClan({ clanId: clan.groupId.toString(), clanName: clan.name, clanTag: clan.clanInfo.clanCallsign || '' })
    );
  }

  addClan(clan: GroupsV2GroupV2Card) {
    this.store.dispatch(
      addClan({ clanId: clan.groupId.toString(), clanName: clan.name, clanTag: clan.clanInfo.clanCallsign || '' })
    );
  }
}

import { Injectable } from '@angular/core';
import { Destiny2Service, GroupV2Service } from 'bungie-api-angular';

import { map, take, catchError, mergeMap, switchMap } from 'rxjs/operators';
import { ClanDatabase } from '../clan-database';
import { of, from } from 'rxjs';
import { BaseClanService } from '../base-clan.service';
import { StoreId } from '../../db/clan-indexed-db';
import { clanDetailSerializer } from './clan-detail-serializer';
import { CLAN_LEVEL_HASH } from '../../hashes/clan-details';

@Injectable()
export class ClanDetailsService extends BaseClanService {
  // private tableName: StoreId = 'ClanDetails';
  private rowId = 'ClanDetails';

  constructor(private groupService: GroupV2Service, private clanDb: ClanDatabase) {
    super(clanDb, StoreId.ClanDetails);
  }
  private getClanDetailsFromAPI(clanId: string) {
    return this.groupService.groupV2GetGroup(clanId as unknown as number);
  }

  private getClanDetails(clanId: string, ignoreOffline: boolean) {
    //console.log(`thinkg ${clanId} ${this.rowId} `);
    return from(this.getDataFromCache(clanId.toString(), this.rowId)).pipe(
      switchMap((cachedData) => {
        //console.log(cachedData);
        if (this.isCacheValid(cachedData, 10)) {
          return of(cachedData?.data);
        }

        return this.getClanDetailsFromAPI(clanId).pipe(
          map((clanDetail) => {
            if (clanDetail.Response) {
              const clanDetails = clanDetailSerializer(clanDetail.Response.detail, [CLAN_LEVEL_HASH]);
              this.updateDB(clanId, this.rowId, clanDetails);

              return clanDetails;
            }
            throw Error('Clan Not found');
          }),
          catchError((error) => {
            if (error?.error?.ErrorStatus === 'SystemDisabled' && !ignoreOffline) {
              throw Error('System Offline');
            }

            if (cachedData && cachedData.data) {
              return of(cachedData.data);
            }
            throw error;
          })
        );
      })
    );
  }

  getClanDetailsSerialized(clanId: string, ignoreOffline: boolean) {
    return this.getClanDetails(clanId, ignoreOffline);
  }
}

