import { BaseAppIndexedDb } from './base-indexed-db';

const DB_VERSION = 2;

export type StoreId = 'allData';

export const STORE_IDS: StoreId[] = ['allData'];

export class AppIndexedDb extends BaseAppIndexedDb {
  constructor(name: string) {
    super(name, DB_VERSION, STORE_IDS);
  }
}

import { BaseAppIndexedDb } from './base-indexed-db';

const DB_VERSION = 6;

export enum StoreId {
  ClanDetails = 'ClanDetails',
  MemberProfiles = 'MemberProfiles',
  MemberActivities = 'MemberActivities',
  MemberRecentActivities = 'MemberRecentActivities',
BungieInfo = 'BungieInfo'
}

export const STORE_IDS: StoreId[] = [
 // StoreId.CacheDetails,
  StoreId.ClanDetails,
  //StoreId.ClanRewards,
  StoreId.MemberProfiles,
  StoreId.MemberActivities,
  StoreId.MemberRecentActivities,
  StoreId.BungieInfo
  //StoreId.ProfileMilestones,
  //StoreId.MemberActivityStats,
  //StoreId.ProfileMetrics,
 // StoreId.Raids
];

export interface DBObject {
  id: string;
  data: any;
  createDate: Date;
}

export class AppIndexedDb extends BaseAppIndexedDb {
  constructor(name: string, initializeValues: boolean = true) {
    super(`D2DB-${name}`, DB_VERSION, STORE_IDS, initializeValues);
  }

  override getAllData(store: StoreId) {
    return super.getAllData(store);
  }

  override getById(store: StoreId, id) {
    return super.getById(store, id);
  }

  override updateValues(values: DBObject[], collectionId: string) {
    return super.updateValues(values, collectionId);
  }
}

import { openDB, deleteDB, IDBPDatabase } from 'idb';

export class BaseAppIndexedDb {
  name: string;
  dbVersion: number;
  storeIds: string[];

  private db: Promise<IDBPDatabase>;

  constructor(name: string, dbVersion: number, storeIds: string[], initializeValues: boolean = true) {
    this.dbVersion = dbVersion;
    this.storeIds = storeIds;

    if (initializeValues) {
      this.resetInitialValues();
    }
    this.name = name;
    this.openDb();
  }

  close() {
    return this.db.then((db) => db.close());
  }

  private resetInitialValues() {}

  getAllData(store: string) {
    return this.db.then((db) => {
      return db.transaction(store, 'readonly').objectStore(store).getAll();
    });
  }

  getById(store: string, id) {
    return this.db.then((db) => {
      return db.transaction(store, 'readonly').objectStore(store).get(id);
    });
  }

  removeData() {
   return this.db
      .then((db) => {
        this.resetInitialValues();
        db.close();
        return deleteDB(this.name);
      })
      .then(() => this.openDb());
  }

  purgeDatabase() {
    return this.close().then((db) => {

      return deleteDB(this.name).catch((x) => {
      });
    });
  }

  updateValues(values: any[], collectionId: string) {
    return this.db.then((db) => {
      const transaction = db.transaction(collectionId, 'readwrite');
      const store = transaction.objectStore(collectionId);
      values.forEach((v) => store.put(v));
      return transaction.done;
    });
  }

  removeValues(ids: string[], collectionId: string) {
    return this.db.then((db) => {
      const transaction = db.transaction(collectionId, 'readwrite');
      const store = transaction.objectStore(collectionId);
      ids.forEach((id) => store.delete(id));
      return transaction.done;
    });
  }

  removeAllValues(collectionId: string) {
    return this.db.then((db) => {
      const transaction = db.transaction(collectionId, 'readwrite');
      const store = transaction.objectStore(collectionId);
      return store.clear();
    });
  }

  private openDb() {
    const base = this;
    this.db = openDB(this.name, this.dbVersion, {
      upgrade(db, oldVersion, newVersion, transaction) {
        base.storeIds.forEach((collectionId) => {
          if (!db.objectStoreNames.contains(collectionId)) {
            const objectStore = db.createObjectStore(collectionId, {
              keyPath: 'id'
            });
          }
        });
      }
    });
  }
}

import { from, Observable } from 'rxjs';
