export abstract class BaseClanAggregateTimeService {
  readonly CONCURRENT_COUNT = 10;
  private memberActivityService: ClanMemberRecentActivityService;
  constructor(private clanDb: ClanDatabase, private apiKey: string) {
    this.memberActivityService = new ClanMemberRecentActivityService(clanDb, apiKey);
  }

  abstract getClanActivityStatsForDuration(memberActivities: MemberActivityTime[], activityMode, count);

  filterDates(memberActivities: MemberActivityTime[], startDate: Date): MemberActivityTime[] {
    return memberActivities.map((ma) => {
      return {
        id: ma.id,
        activities: ma.activities.filter((x) => x.date > startDate)
      };
    });
  }

  /** @deprecated use gender field instead */
  getClanActivityStats(clanId: number, clanMemberProfiles: MemberProfile[], startDate: Date, activityMode: number = 0) {
    return from(clanMemberProfiles).pipe(
      mergeMap((member) => {
        return this.getMemberActivityStats(clanId, member, startDate, activityMode);
      }, this.CONCURRENT_COUNT)
    );
  }

  /** @deprecated use gender field instead */
  private getMemberActivityStats(
    clanId: number,
    member: MemberProfile,
    startDate: Date,
    activityMode: number = 0
    // TODO: Update
  ): Observable<any> {
    return this.memberActivityService.getMemberActivity(clanId, member, false, activityMode).pipe(
      map((memberActivityResponse) => {
        return {
          memberProfile: { profile: member.profile },
          stats: {
            id: memberActivityResponse.id,
            activities: memberActivityResponse.activities.filter((x) => new Date(x.period) > startDate)
          }
        };
      })
    );
  }
}

import { ClanDatabase } from './clan-database';
import { DBObject, StoreId } from '../db/clan-indexed-db';
import { isValidDate, nowPlusMinutes } from '../utility/date-utils';

export class BaseClanService {
  tableName;
  constructor(private clanDbBase: ClanDatabase, private tableNameBase: StoreId) {
    this.tableName = tableNameBase;
  }

  getDataFromCache(clanId: string, rowId: string): Promise<DBObject> {
    return this.clanDbBase.getById(clanId, this.tableNameBase, rowId);
  }

  getAllDataFromCache(clanId: string): Promise<DBObject[]> {
    return this.clanDbBase.getAll(clanId, this.tableNameBase);
  }

  isCacheValid(cachedData: DBObject, minuteExpiration: number, lastActivity?: Date) {
    if (cachedData && cachedData.createDate) {
      const cacheDate = cachedData.createDate;
      let expireDate;
      if (isValidDate(lastActivity)) {
        if (minuteExpiration === 0) {
          expireDate = lastActivity;
        } else {
          const minuteXP = nowPlusMinutes(-minuteExpiration);
          const lastActivityXP = lastActivity;
          expireDate = minuteXP > lastActivityXP ? lastActivityXP : minuteXP;
        }
      } else {
        expireDate = nowPlusMinutes(-minuteExpiration);
      }
      return cacheDate > expireDate;
    }
    return false;
  }

  updateDB(clanId: string, rowId: string, data: any) {
    this.clanDbBase.update(clanId, this.tableName, [
      {
        id: rowId,
        createDate: new Date(),
        data: data
      }
    ]);
  }
}

