interface MemberProfile {
  profile: any;
}

interface ActivityCollection {
  activities: any[];
}
export class BaseMemberActivityService extends BaseClanService {
  private ACTIVITY_GET_COUNT = 250;
  constructor(
    private clanDbPBase: ClanDatabase,
    private tableNamePBase: StoreId,
    private apiKey: string,
    // private d2ServiceBase: Destiny2Service,
    public startValue: Date,
    public maxRequestCount: Number,
    public activityTypeId = 0
  ) {
    super(clanDbPBase, tableNamePBase);
  }

  public getMemberCharacterActivityFromAPI(member: MemberProfile, characterId: number, pageNumber = 0) {
    // https://www.bungie.net/Platform/Destiny2/3/Account/4611686018483900283/Character/2305843009471454818/Stats/Activities/?count=250&mode=0&page=10

    const url = `https://www.bungie.net/Platform/Destiny2/${member.profile.data.userInfo.membershipType}/Account/${member.profile.data.userInfo.membershipId}/Character/${characterId}/Stats/Activities/?count=${this.ACTIVITY_GET_COUNT}&mode=${this.activityTypeId}&page=${pageNumber}`;

    return new Observable((observer) => {
      fetch(url, { headers: { 'X-API-Key': this.apiKey } })
        .then((response) => response.json())
        .then((data) => {
          observer.next(data);
          observer.complete();
        })
        .catch((err) => observer.error(err));
    });

    // return this.d2ServiceBase.destiny2GetActivityHistory(
    //   characterId,
    //   member.profile.data.userInfo.membershipId,
    //   member.profile.data.userInfo.membershipType,
    //   this.ACTIVITY_GET_COUNT,
    //   this.activityTypeId,
    //   pageNumber
    // );
  }

  private activitiesContainExpiredYear(activities, expiration) {
    if (!activities) {
      return true;
    }

    return !!activities.find((x) => {
      const activityYear = new Date(x.period);
      return activityYear <= expiration;
    });
  }

  private getAllRecentActivity(member: MemberProfile, characterId: number): Observable<ActivityCollection> {
    const maxConcurrentCount = 4;
    const fetchPage = (page = 0) => {
      return this.getMemberCharacterActivityFromAPI(member, characterId, page).pipe(
        map((x: any) => {
          const nextPage =
            this.activitiesContainExpiredYear(x?.Response?.activities, this.startValue) || page >= this.maxRequestCount
              ? null
              : page + maxConcurrentCount;

          const activities = x?.Response?.activities || [];
          return { activities, nextPage };
        })
      );
    };

    const getItems = (page) =>
      defer(() => fetchPage(page)).pipe(
        mergeMap(({ activities, nextPage }) => {
          const items$ = from(activities);
          const next$ = nextPage ? getItems(nextPage) : EMPTY;
          return concat(items$, next$);
        })
      );

    const batchedRequest = [];
    for (let i = 0; i < maxConcurrentCount; i++) {
      batchedRequest.push(getItems(i).pipe(toArray()));
    }

    return forkJoin(batchedRequest).pipe(
      map((x: any) => {
        return {
          activities: x.flat()
        };
      })
    );
  }

  getMemberActivityId(member: MemberProfile, characterId: number) {
    return `${this.getMemberProfileId(member)}-${characterId}`;
  }
  getMemberProfileId(member: MemberProfile) {
    return `${member.profile.data.userInfo.membershipType}-${member.profile.data.userInfo.membershipId}`;
  }

  /**
   *  Pulls character activity from cache and will return fresh data if cache is exipred
   */
  getMemberCharacterActivity(
    clanId: number,
    member: MemberProfile,
    characterId: number,
    useCache: boolean

    // ): Observable<Array<DestinyHistoricalStatsDestinyHistoricalStatsPeriodGroup>> {
  ): Observable<Array<any>> {
    const characterActivityId = this.getMemberActivityId(member, characterId);

    return from(this.getDataFromCache(clanId.toString(), characterActivityId)).pipe(
      mergeMap((cachedData) => {
        // if (this.isCacheValid(cachedData, 720, new Date(member.profile.data.dateLastPlayed))) {
        //   return of(cachedData.data);
        // }

        // return this.getFreshMemberCharacterActivity(clanId, member, characterId, characterActivityId, cachedData);

        if (useCache) {
          return this.verifyCacheIntegrity(clanId, member, characterId, cachedData);
        }
        return of(cachedData.data);
      })
    );
  }

  /**
   * Determines if Cached data is fresh enough to use. Triggers new call if too old.
   *
   */
  verifyCacheIntegrity(clanId, memberProfile: MemberProfile, characterId, cachedData: DBObject) {
    const characterActivityId = this.getMemberActivityId(memberProfile, characterId);
    if (this.isCacheValid(cachedData, 720, new Date(memberProfile.profile.data.dateLastPlayed))) {
      return of(cachedData.data);
    }

    return this.getFreshMemberCharacterActivity(clanId, memberProfile, characterId, characterActivityId, cachedData);
  }

  /**
   * Calls for fresh character activity. Updates cache. Falls back on cache on failure.
   */
  getFreshMemberCharacterActivity(
    clanId: string,
    member: MemberProfile,
    characterId: number,
    characterActivityId: string,
    cachedData: DBObject
    //): Observable<Array<DestinyHistoricalStatsDestinyHistoricalStatsPeriodGroup>> {
  ): Observable<Array<any>> {
    return this.getAllRecentActivity(member, characterId).pipe(
      map((activityResponse) => {
        if (activityResponse.activities) {
          // serialize:
          const slimmedActivities = activityResponse.activities.map((x) => {
            return clanMemberActivitySerializer(x);
          });

          this.updateDB(clanId, characterActivityId, slimmedActivities);
          return slimmedActivities;
        }
        return of([]);
      }),
      catchError((error) => {
        if (error.error?.ErrorStatus === 'DestinyPrivacyRestriction') {
          this.updateDB(clanId, characterActivityId, []);
          return of([]);
        }
        if (cachedData && cachedData.data) {
          return of(cachedData.data);
        }

        throw error;
      })
    );
  }

  groupActivitiesToMember(memberProfile: MemberProfile, allActivities: DBObject[], activityMode: number = 0) {
    if (!memberProfile) {
      return undefined;
    }
    const memberProfileId = `${memberProfile.profile.data.userInfo.membershipType}-${memberProfile.profile.data.userInfo.membershipId}`;

    const memberActivitiesDB = allActivities.filter((x) => x.id.startsWith(memberProfileId));

    const memberActivitiesSerialized = memberActivitiesDB.map((activityDB) =>
      activityDB.data.map((activity) => clanMemberActivitySerializer(activity))
    );

    const allFilteredActivities =
      activityMode > 0
        ? memberActivitiesSerialized.map((items) =>
            items.filter((a) => a.activityDetails.modes.indexOf(activityMode) > -1)
          )
        : memberActivitiesSerialized;

    const timed = groupActivitiesByDate([].concat(...allFilteredActivities));

    return {
      id: memberProfileId,
      activities: timed
    };
  }

  groupActivitiesToMembers(
    memberProfiles: MemberProfile[],
    allActivities: DBObject[],
    activityMode: number = 0
  ): any[] {
    return memberProfiles
      .filter((m) => !!m)
      .map((memberProfile) => {
        return this.groupActivitiesToMember(memberProfile, allActivities, activityMode);
      });
  }

  getAllActivitiesFromCache(
    clanId: number,
    memberProfiles: MemberProfile[],
    activityMode = 0
  ): Observable<MemberActivityTime[]> {
    return from(this.getAllDataFromCache(clanId.toString())).pipe(
      map((x) => {
        const y = this.groupActivitiesToMembers(memberProfiles, x, activityMode);
        return y;
      })
    );
  }
  // TODO: Turn this up to 11
  updateAllActivityCache(clanId: number, memberProfiles: MemberProfile[], progress?: (done) => any) {
    const memberProfilesObs = from(memberProfiles);
    const cacheDataObs = from(this.getAllDataFromCache(clanId.toString()));

    return cacheDataObs.pipe(
      switchMap((cachedData) => {
        let complete = 0;
        return memberProfilesObs.pipe(
          mergeMap((memberProfile) => {
            if (!memberProfile?.profile?.data?.characterIds) {
              return of();
            }
            return from(memberProfile.profile.data.characterIds).pipe(
              mergeMap((characterId: number) => {
                const characterActivityId = this.getMemberActivityId(memberProfile, characterId);
                const characterActivityCache = cachedData.find((x) => x.id === characterActivityId);

                return this.verifyCacheIntegrity(clanId, memberProfile, characterId, characterActivityCache);
              }),
              toArray(),
              map((x) => {
                const memberProfileId = `${memberProfile.profile.data.userInfo.membershipType}-${memberProfile.profile.data.userInfo.membershipId}`;
                return {
                  id: memberProfileId
                };
              })
            );
          }, 3),
          tap((x) => {
            complete++;
            if (progress) {
              progress(complete);
            }
          }),
          toArray()
        );
      })
    );
  }
  getMemberActivity(
    clanId: number,
    member: any,
    useCache: boolean,
    activityMode: number = 0
  ): Observable<MemberActivityStats> {
    if (!member?.profile) {
      return of(null);
    }
    return from(member.profile.data.characterIds).pipe(
      mergeMap((characterId: number) => {
        return this.getMemberCharacterActivitySerialized(clanId, member, characterId, useCache, activityMode);
      }),
      map((x) => {
        return x.activities;
      }),
      toArray(),
      map((x) => {
        return {
          id: `${member.profile.data.userInfo.membershipType}-${member.profile.data.userInfo.membershipId}`,
          activities: [].concat(...x)
        };
      }),
      catchError((error) => {
        console.error(error);
        return of(null);
      })
    );
  }
  getMemberCharacterActivitySerialized(
    clanId: number,
    member: MemberProfile,
    characterId: number,
    useCache: boolean,
    activityMode: number = 0
  ) {
    return this.getMemberCharacterActivity(clanId, member, characterId, useCache).pipe(
      map((activity) => {
        if (activityMode > 0) {
          activity = activity.filter((a) => a.activityDetails.modes.indexOf(activityMode) > -1);
        }
        return {
          activities: activity.map((a) => clanMemberActivitySerializer(a))
        };
      })
    );
  }
}

import { map, catchError, mergeMap, bufferTime, toArray } from 'rxjs/operators';
import { Observable, from, of, throwError } from 'rxjs';
// import { profileSerializer } from './profile.serializer';
import { nowPlusDays, unixTimeStampToDate } from '../../utility/date-utils';
import { StoreId } from '../../db/clan-indexed-db';
import { ClanDatabase } from '../clan-database';
import { ClanMember } from '../../models/ClanMember';
import { BungieInfo } from '../../models/BungieInfo';
interface MemberProfile {}

export class BungieInfoService {
  private tableName: StoreId = StoreId.BungieInfo;
  private concurrentRequests = 20;

  constructor(private clanDb: ClanDatabase, private apiKey: string) {}

  private getProfileId(member: ClanMember) {
    return `${member.destinyUserInfo.membershipType}-${member.destinyUserInfo.membershipId}`;
  }
  private getBungieNetMembershipId(member: ClanMember) {
    return member.bungieNetUserInfo.membershipId;
  }

  private getBungieInfoFromCache(clanId: string, member: ClanMember) {
    return this.clanDb.getById(clanId, this.tableName, this.getProfileId(member));
  }

  private getBungieInfoFromAPI(member: ClanMember) {
    if (member?.bungieNetUserInfo?.membershipId) {
      const url = `https://www.bungie.net/Platform/User/GetBungieNetUserById/${this.getBungieNetMembershipId(member)}/`;
      // https://www.bungie.net/Platform/User/GetBungieNetUserById/612698/

      return new Observable((observer) => {
        fetch(url, { headers: { 'X-API-Key': this.apiKey } })
          .then((response) => response.json())
          .then((data) => {
            if (!data.Response) {
              throw data;
            }
            observer.next(data);
            observer.complete();
          })
          .catch((err) => {
            console.log('error', err);
            observer.error(err);
          });
      });
    }
    return of(null);
  }

  getBungieInfo(clanId: string, member: ClanMember): Observable<any> {
    return from(this.getBungieInfoFromCache(clanId, member)).pipe(
      mergeMap((cachedData) => {
        if (cachedData && cachedData.createDate) {
          const cacheDate = cachedData.createDate;
          const lastStatusChange = unixTimeStampToDate(member.lastOnlineStatusChange);
          const staleXP = nowPlusDays(-1);
          // Make sure we recapture new data after season change
          const expireDate = staleXP > lastStatusChange ? staleXP : lastStatusChange;

          if (cacheDate > expireDate) {
            return of(cachedData?.data);
          }
        }
        return this.getBungieInfoFromAPI(member).pipe(
          map((memberProfileResponse: any) => {
            if (memberProfileResponse?.Response) {
              this.clanDb.update(clanId, this.tableName, [
                {
                  id: this.getProfileId(member),
                  createDate: new Date(),
                  data: memberProfileResponse.Response
                }
              ]);

              return memberProfileResponse.Response;
            }
          }),
          catchError((error) => {
            if (cachedData && cachedData.data) {
              return of(cachedData.data);
            }
            if (error?.ErrorStatus === 'DestinyAccountNotFound') {
              console.error(`Error retrieving profile, not found`, member);
              return of();
            }
            if (error?.ErrorStatus === 'DestinyUnexpectedError') {
              console.error(`Error retrieving profile`, member);
              return of();
            }

            throw error;
          })
        );
      })
    );
  }

  getSerializedBungieInfos<T>(clanId: string, members: ClanMember[]): Observable<T> {
    return from(members).pipe(mergeMap((member) => this.getSerializedBungieInfo(clanId, member), 100)) as Observable<T>;
  }

  getSerializedBungieInfosFromCache(clanId: string, members: ClanMember[]): Observable<BungieInfo[]> {
    return from(members).pipe(
      mergeMap((member) => this.getSerializedBungieInfoFromCache(clanId, member), 100),
      toArray()
    );
  }

  getSerializedBungieInfosWithProgress(
    clanId: string,
    members: ClanMember[],
    progress?: (done) => any
  ): Observable<BungieInfo[]> {
    let complete = 0;
    return from(members)
      .pipe(mergeMap((member) => this.getSerializedBungieInfo(clanId, member), this.concurrentRequests))
      .pipe(
        bufferTime(1000, undefined, 100),
        /**
         * Don't continue processing if the timer in `bufferTime` was reached and
         *   there are no buffered companies.
         */
        mergeMap((memberResp) => {
          complete += memberResp.length;
          if (progress) {
            progress(complete);
          }
          return memberResp;
        }),
        toArray()
      );
  }

  getSerializedBungieInfo(clanId: string, member: ClanMember): Observable<BungieInfo> {
    return this.getBungieInfo(clanId, member).pipe(
      map((profile) => {
        return profile;
        // return profileSerializer(
        //   profile,
        //   this.TRACKED_HASHES,
        //   collectionHashes,
        //   profileRecords,
        //   profileMetrics
        // ) as MemberProfile;
      })
    );
  }
  getSerializedBungieInfoFromCache(clanId: string, member: ClanMember): Observable<BungieInfo> {
    return from(this.getBungieInfoFromCache(clanId, member)).pipe(
      map((profile) => {
        return profile?.data || [];
        // return profileSerializer(
        //   profile?.data || [],
        //   this.TRACKED_HASHES,
        //   collectionHashes,
        //   profileRecords,
        //   profileMetrics
        // ) as MemberProfile;
      })
    );
  }
}

import { Injectable } from '@angular/core';
import { createStore, UseStore } from 'idb-keyval';
import { catchError, from, map, mergeMap, Observable, of } from 'rxjs';
import { IdbKeyValService } from '../storage';
import { nowPlusDays, unixTimeStampToDate } from '../utility/date-utils';
import { ProfileService } from './profile.service';
import { MemberProfile } from '../models/MemberProfile';

export class CachedProfileService extends ProfileService {
  profileStore: UseStore;
  constructor(private idbKeyValService: IdbKeyValService, private apiKey: string) {
    super(apiKey);
    this.profileStore = createStore('D2Dashboard-profiles', 'data');
  }
  private getStorageId(membershipType, membershipId) {
    return `${membershipType}-${membershipId}`;
  }
  private getProfileFromCache(membershipType, membershipId) {
    return this.idbKeyValService.get(this.getStorageId(membershipType, membershipId), this.profileStore);
  }

  getProfile(membershipType, membershipId): Observable<MemberProfile> {
    console.log(`${membershipType}${membershipId}`);
    return from(this.getProfileFromCache(membershipType, membershipId)).pipe(
      mergeMap((cachedData) => {
        console.log('cached', cachedData);
        if (cachedData && cachedData.createDate) {
          const cacheDate = cachedData.createDate;
          //const lastStatusChange = unixTimeStampToDate(member.lastOnlineStatusChange);
          const staleXP = nowPlusDays(-1);
          // Make sure we recapture new data after season change
          //const expireDate = staleXP > lastStatusChange ? staleXP : lastStatusChange;

          if (cacheDate > staleXP) {
            console.log('returning cached');
            return of(cachedData?.data);
          }
        }
        return this.getProfileFromAPI(membershipType, membershipId).pipe(
          map((memberProfileResponse: any) => {
            if (memberProfileResponse.Response) {
              this.idbKeyValService.set(
                this.getStorageId(membershipType, membershipId),
                {
                  id: this.getStorageId(membershipType, membershipId),
                  createDate: new Date(),
                  data: memberProfileResponse.Response
                },
                this.profileStore
              );

              return memberProfileResponse.Response;
            }
          }),
          catchError((error) => {
            console.log('of error', error);
            if (cachedData && cachedData.data) {
              return of(cachedData.data);
            }
            if (error?.ErrorStatus === 'DestinyAccountNotFound') {
              console.error(`Error retrieving profile, not found`, this.getStorageId(membershipType, membershipId));
              return of();
            }
            if (error?.ErrorStatus === 'DestinyUnexpectedError') {
              console.error(`Error retrieving profile`, this.getStorageId(membershipType, membershipId));
              return of();
            }

            throw error;
          })
        );
      })
    );
  }
}

