      transform: rotate(180deg);
    }
  }
  .footer-display {
    background: var(--app-primary-color) !important;
    display: flex;
    font-size: 12px;
    justify-content: space-between;
    top: -30px;
    position: absolute;
    padding: 0.25rem;
    width: 100%;
  }
  .footer_app-info {
    flex: 1;
    text-align: right;
    padding-right: 1rem;
  }
  .footer-button {
    background: var(--app-background-color);
    color: var(--app-primary-color-text) !important;
    border-radius: 50%;
    padding: 0;
    margin: 0;
    border: none;
    height: 22px;
    width: 22px;
    cursor: pointer;
    transform-origin: center;
    transition: 0.25s;
    mat-icon {
      font-size: 18px;
      line-height: 21px;
      height: 20px;
      width: 20px;
    }
  }
  .footer-content {
    padding-top: 1rem;
    padding-bottom: 1rem;
    text-align: center;
  }
}

