    //       //basePath: environment.apiUrl,
    //       //accessToken: authService.getAccessToken.bind(authService)
    //     }
    //   ),
    //   multi: false
    // },
    {
      provide: HTTP_INTERCEPTORS,
      useClass: ApiKeyInterceptor,
      multi: true
    }
  ]
})
export class CoreModule {
  constructor(
    @Optional()
    @SkipSelf()
