  }),
  on(updateNotification, (state, { notification }) => {
    return NotificationAdapter.upsertOne(notification, { ...state });
  }),
  on(removeNotification, (state, { notification }) => {
    return NotificationAdapter.removeOne(notification.id, { ...state });
  })
)

