import { DatePipe } from '@angular/common';
import { Inject, LOCALE_ID, Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'bungieDate'
})
export class BungieDatePipe extends DatePipe implements PipeTransform {

  constructor(@Inject(LOCALE_ID) locale: string) {
    super(locale);
  }

  override transform(date: Date | string): any {
    const d2 = new Date(date)
    return super.transform(d2, 'yyyy-MM-dd');
  }
}

import { DatePipe } from '@angular/common';
import { Inject, LOCALE_ID, Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'bungieDateTime'
})
export class BungieDateTimePipe extends DatePipe implements PipeTransform {
  constructor(@Inject(LOCALE_ID) locale: string) {
    super(locale);
  }

  override transform(date: Date): any {
    return super.transform(date, 'yyyy-MM-dd h:mm a') || '';
  }
}

import { ChangeDetectionStrategy, Component, Input, OnInit, Pipe, PipeTransform } from '@angular/core';
import { MemberProfile } from '@destiny/data/models';

@Component({
  selector: 'app-class-cell',
  template: `
    <div [ngClass]="(profile?.characters.data)[characterHash].classType | classicon">
      {{ (profile?.characters.data)[characterHash].light }}
    </div>
  `,
  styles: [
  ],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ClassCellComponent {
  @Input() characterHash: string;

  @Input() profile: MemberProfile;

  constructor() {}

  getIcon(): string {
    const classType = (this.profile?.characters.data)[this.characterHash].classType;
    switch (classType) {
      case 0:
        return 'titan';
      case 1:
        return 'hunter';
      case 2:
        return 'warlock';
      default:
        return '';
    }
  }
}
@Pipe({
  name: 'classicon',
  pure: true
})
export class ClassIconPipe implements PipeTransform {
  constructor() {}

  transform(classType: number): any {
    switch (classType) {
      case 0:
        return 'titan-cell';
      case 1:
        return 'hunter-cell';
      case 2:
        return 'warlock-cell';
    }
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { MembershipTypes } from '@destiny/data/models/enums';

@Pipe({
  name: 'memberType'
})
export class MemberTypePipe implements PipeTransform {
  transform(value: number): string {
    switch (value) {
      case MembershipTypes.Xbox:
        return 'xbox';
      case MembershipTypes.Psn:
        return 'playstation';
      case MembershipTypes.Steam:
        return 'steam';
      case MembershipTypes.Stadia:
        return 'stadia';
      default:
        return '';
    }
  }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'playtime'
})
export class PlaytimePipe implements PipeTransform {
  transform(input: number, showSeconds: boolean = true): string {
    let totalSeconds = input;
    const hours = String(Math.floor(totalSeconds / 3600)).padStart(2, '0');
    totalSeconds %= 3600;
    const minutes = String(Math.floor(totalSeconds / 60)).padStart(2, '0');
    const seconds = showSeconds ? ':' + String(Math.floor(totalSeconds % 60)).padStart(2, '0') : '';

    return hours + ':' + minutes + seconds;
  }
}
@Pipe({
  name: 'playtimems'
})
export class PlaytimeMillisecondsPipe implements PipeTransform {
  transform(input: number): string {
    if (input === 0) {
      return '';
    }
    let totalSeconds = input / 1000;
    const hours = String(Math.floor(totalSeconds / 3600)).padStart(2, '0');
    totalSeconds %= 3600;
    const minutes = String(Math.floor(totalSeconds / 60)).padStart(2, '0');
    const seconds = String(Math.floor(totalSeconds % 60)).padStart(2, '0');

    return hours + ':' + minutes + ':' + seconds;
  }
}

