export class DailyClanAggregateTimeService extends BaseClanAggregateTimeService {
  getClanActivityStatsForDuration(memberActivities: MemberActivityTime[], activityMode: any, days = 60) {
    const x = this.filterDates(memberActivities, nowPlusDays(-days));

    const activities = [...x.map((y) => y.activities)];
    const flatActivities = [].concat.apply([], activities);
    const summedActivities = groupActivityStatsByDate(flatActivities);
    return summedActivities;
  }
  getClanActivityByPlayer(memberActivities: MemberActivityTime[], activityMode: any, days = 60) {
    const x = this.filterDates(memberActivities, nowPlusDays(-days));

    const results = x.map((y) => {
      return {
        id: y.id,
        seconds: y.activities.reduce((prev, next) => prev + next.seconds, 0)
      };
    });

    return results;
  }
}

import {Observable, of} from 'rxjs';

export interface DataSourceMetadata<T> {
  label: string;
  type: string;
  accessor: (item: T) => any;
}

export interface DataLabel {
  id: string;
  label: string;
}

export interface DataSourceOptions<T> {
  data?: Observable<T[]>|T[];
  metadata?: Map<string, DataSourceMetadata<T>>;
}

export class DataSource<T = any> {
  private metadata: Map<string, DataSourceMetadata<T>>;

  data: Observable<T[]>;

  constructor(options: DataSourceOptions<T> = {}) {
    if (options.data instanceof Observable) {
      this.data = options.data as Observable<T[]>;
    } else if (Array.isArray(options.data)) {
      this.data = of(options.data);
    } else {
      this.data = of([]);
    }

    this.metadata = options.metadata || new Map();
  }

  getDataLabelsWithType(type: string): DataLabel[] {
    const dataLabelsWithType: DataLabel[] = [];
    this.metadata.forEach((value, key) => {
      if (value.type === type) {
        dataLabelsWithType.push({id: key, label: value.label});
      }
    });
    return dataLabelsWithType;
  }

  getDataProperty<V = any>(id: string, item: T): V {
    return this.metadata.get(id).accessor(item);
  }
}

import { combineLatest, EMPTY, Observable, startWith, take } from 'rxjs';

export interface ExporterMetadata<T = any, C = any> {
  label: string;
  text: (item: T, context: C) => string | null;
}
export type ExporterContextProvider<T, C> = Observable<(item: T) => C>;

export interface ExporterOptions<T, C> {
  metadata?: Map<string, ExporterMetadata<T, C>>;
  contextProvider?: ExporterContextProvider<T, C>;
  // initialState?: SorterState;
}

export class Exporter<T = any, C = any> {
  private metadata: Map<string, ExporterMetadata<T, C>>;
  private contextProvider: ExporterContextProvider<T, C>;

  constructor(options: ExporterOptions<T, C> = {}) {
    this.metadata = options.metadata || new Map();
    this.contextProvider = options.contextProvider || EMPTY.pipe(startWith(() => null));
  }

  exportData(name: string, items$: Observable<T[]>): void {
    combineLatest([items$, this.contextProvider])
      .pipe(take(1))
      .subscribe(([items, context]) => {
        const keys = [];
        this.metadata.forEach((value, key) => keys.push(key));
        const formattedObj = items.map((item) => {
          const obj = {};
          keys.forEach((key) => {
            const config = this.metadata.get(key);
            obj[config.label] = config.text(item, context(item));
          });
          return obj;
        });
        downloadCSV({ filename: `${name}-${new Date().getTime()}` }, formattedObj);
      });
  }
}

function downloadCSV(args, stockData) {
  let data, filename, link;
  let csv = convertArrayOfObjectsToCSV({
    data: stockData
  });
  if (csv == null) {
    return;
  }

  filename = args.filename || 'export.csv';

  if (!csv.match(/^data:text\/csv/i)) {
    csv = 'data:text/csv;charset=utf-8,' + csv;
  }
  data = encodeURI(csv);

  link = document.createElement('a');
  link.setAttribute('href', data);
  link.setAttribute('download', filename);
  link.click();
}
function convertArrayOfObjectsToCSV(args) {
  let result, ctr, keys, columnDelimiter, lineDelimiter, data;

  data = args.data || null;
  if (data == null || !data.length) {
    return null;
  }

  columnDelimiter = args.columnDelimiter || ',';
  lineDelimiter = args.lineDelimiter || '\n';

  keys = Object.keys(data[0]);

  result = '';
  result += keys.join(columnDelimiter);
  result += lineDelimiter;

  data.forEach(function (item) {
    ctr = 0;
    keys.forEach(function (key) {
      if (ctr > 0) {
        result += columnDelimiter;
      }

      result += item[key];
      ctr++;
    });
    result += lineDelimiter;
  });

  return result;
}

import {combineLatest, EMPTY, Observable, ReplaySubject} from 'rxjs';
import {map, startWith, take} from 'rxjs/operators';
import {
  DateFilter,
  Filter,
  FilterType,
  NumberFilter,
  StateFilter,
  TextFilter
} from './filterer-types';

export type TextFilterAutocomplete<T = any, C = any> = (items: T[], c: C) => string[];

export interface TextFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'text';
  matcher: (item: T, q: TextFilter, c: C) => boolean;
  autocomplete?: TextFilterAutocomplete<T, C>;
}

export interface NumberFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'number';
  matcher: (item: T, q: NumberFilter, c: C) => boolean;
}

export interface DateFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'date';
  matcher: (item: T, q: DateFilter, c: C) => boolean;
}

export interface StateFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'state';
  matcher: (item: T, q: StateFilter, c: C) => boolean;
  states: string[];
}

export type FiltererMetadata<T = any, C = any> = TextFiltererMetadata<T, C> |
  NumberFiltererMetadata<T, C> | DateFiltererMetadata<T, C> | StateFiltererMetadata<T, C>;

export interface FiltererState {
  filters: Filter[];
  search: string;
}

export interface FilterOption {
  id: string;
  label: string;
  type: FilterType;
}

export type FiltererContextProvider<M> = Observable<M>;

export interface FiltererOptions<T, C> {
  metadata?: Map<string, FiltererMetadata<T, C>>;
  contextProvider?: FiltererContextProvider<C>;
  initialState?: FiltererState;
  tokenizeItem?: (item: T) => string;
}

/** Default and naive tokenize function that combines the item's property values into a string. */
const DEFAULT_TOKENIZE_ITEM =
  (data: any) => {
    return Object.keys(data)
      .reduce(
        (currentTerm: string, key: string) => {
          return currentTerm + (data as {[key: string]: any})[key] + '';
        },
        '')
      .toLowerCase();
  };

export class Filterer<T = any, C = any> {
  private readonly metadata: Map<string, FiltererMetadata<T, C>>;

  private contextProvider: Observable<C>;

  private readonly tokenizeItem: (item: T) => string;

  state = new ReplaySubject<FiltererState>(1);

  constructor(options: FiltererOptions<T, C> = {}) {
    this.metadata = options.metadata || new Map();
    this.state.next(options.initialState || {filters: [], search: ''});
    this.contextProvider = options.contextProvider || EMPTY.pipe(startWith(null));
    this.tokenizeItem = options.tokenizeItem || DEFAULT_TOKENIZE_ITEM;
  }

  /** Gets a stream that returns the items and updates whenever the filters or search changes. */
  filter(): (items: Observable<T[]>) => Observable<T[]> {
    return (items$: Observable<T[]>) => {
      return combineLatest(items$, this.state, this.contextProvider).pipe(
        map(([items, state, contextProvider]) => {
          const filters = state.filters;
          const search = state.search;
          const filteredItems = filterItems(items, filters, contextProvider, this.metadata);
          return searchItems(filteredItems, search, this.tokenizeItem);
        }));
    };
  }

  setState(state: FiltererState) {
    this.state.next({...state});
  }

  isEquivalent(otherState?: FiltererState): Observable<boolean> {
    return this.state.pipe(map(state => {
      if (!otherState) {
        return false;
      }

      const filtersEquivalent =
        JSON.stringify(state.filters.sort()) === JSON.stringify(otherState.filters.sort());
      const searchEquivalent = state.search === otherState.search;

      return filtersEquivalent && searchEquivalent;
    }));
  }

  getFilterOptions(): FilterOption[] {
    const filterOptions: FilterOption[] = [];
    this.metadata.forEach((value, key) => {
      filterOptions.push({id: key, label: value.label, type: value.type});
    });
    return filterOptions;
  }

  getStateFilterOptions(id: string): string[] {
    const filtererMetadata = this.metadata.get(id);

    if (filtererMetadata.type !== 'state') {
      throw Error(`Cannot get state options for filters with type ${filtererMetadata.type}`);
    }

    return filtererMetadata.states;
  }

  transformTextFilterOptions(id: string): (items: Observable<T[]>) => Observable<string[]> {
    const filtererMetadata = this.metadata.get(id);

    if (filtererMetadata.type !== 'text') {
      throw Error(`Cannot get text options for filters with type ${filtererMetadata.type}`);
    }

    return (items$: Observable<T[]>) => {
      return combineLatest(items$, this.contextProvider).pipe(map(([items, context]) => {
        if (!filtererMetadata.autocomplete) {
          return [];
        }
        return filtererMetadata.autocomplete(items, context);
      }));
    };
  }

  add(filter: Filter) {
    this.state.pipe(take(1)).subscribe(state => {
      const filters = state.filters.slice();
      filters.push(filter);
      this.setState({...state, filters});
    });
  }

  remove(filter: Filter) {
    this.state.pipe(take(1)).subscribe(state => {
      const filters = state.filters.slice();
      const index = state.filters.indexOf(filter);

      if (index !== -1) {
        filters.splice(index, 1);
        this.setState({...state, filters});
      }
    });
  }
}

/** Utility function to filter the items. May be used to synchronously filter items. */
export function filterItems<T, M>(
  items: T[], filters: Filter[] = [], context: M, metadata: Map<string, FiltererMetadata<T, M>>) {
  return items.filter(item => {
    return filters.every(filter => {
      const filterMetadata = metadata.get(filter.id);
      if (!filterMetadata || !filterMetadata.matcher) {
        throw Error('Missing matcher for ' + filter.id);
      }

      switch (filterMetadata.type) {
        case 'text':
          return filterMetadata.matcher(item, filter as TextFilter, context);
        case 'date':
          return filterMetadata.matcher(item, filter as DateFilter, context);
        case 'number':
          return filterMetadata.matcher(item, filter as NumberFilter, context);
        case 'state':
          return filterMetadata.matcher(item, filter as StateFilter, context);
      }
    });
  });
}

export function searchItems<T>(items: T[], search: string, tokenizeItem: (item: T) => string): T[] {
  return !search ? items : items.filter(item => {
    const tokens = search.split(' OR ');
    return tokens.some(token => {
      return tokenizeItem(item).indexOf(token.toLowerCase()) !== -1;
    });
  });
}

import { combineLatest, EMPTY, Observable, ReplaySubject } from 'rxjs';
import { map, mergeMap, startWith } from 'rxjs/operators';

export interface GrouperState {
  group: string;
}

export class Group<T> {
  id: string;
  title: string;
  items: T[];
}

export interface GrouperMetadata<T = any, C = any> {
  label: string;
  groupingFunction: (items: T[]) => Group<T>[];
  titleTransform?: (title: string, c: C) => string;
}

export interface GroupLabel {
  id: string;
  label: string;
}

export interface GrouperOptions<T, C> {
  metadata?: Map<string, GrouperMetadata<T, C>>;
  contextProvider?: Observable<C>;
  initialState?: GrouperState;
}

export class Grouper<T = any, C = any> {
  private metadata: Map<string, GrouperMetadata<T, C>>;

  private contextProvider: Observable<C>;

  state = new ReplaySubject<GrouperState>(1);

  constructor(options: GrouperOptions<T, C> = {}) {
    this.metadata = options.metadata || new Map();
    this.contextProvider = options.contextProvider || EMPTY.pipe(startWith(null));

    if (options.initialState) {
      this.state.next(options.initialState);
    } else if (this.metadata.size > 0) {
      this.state.next({ group: this.getGroups()[0].id });
    }
  }

  group(): (items$: Observable<T[]>) => Observable<Group<T>[]> {
    return (items$: Observable<T[]>) => {
      return combineLatest(items$, this.state).pipe(
        mergeMap(([items, state]) => this.performGrouping(items, state.group)),
        map((itemGroups) => itemGroups.sort((a, b) => (a.title < b.title ? -1 : 1)))
      );
    };
  }

  getGroups(): GroupLabel[] {
    const groups: GroupLabel[] = [];
    this.metadata.forEach((value, key) => groups.push({ id: key, label: value.label }));
    return groups;
  }

  setState(state: GrouperState) {
    this.state.next({ ...state });
  }

  isEquivalent(otherState?: GrouperState): Observable<boolean> {
    return this.state.pipe(
      map((state) => {
        if (!otherState) {
          return false;
        }

        return state.group === otherState.group;
      })
    );
  }

  private performGrouping(items: T[], groupId: string): Observable<Group<T>[]> {
    const groupMetadata = this.getGroupMetadata(groupId);
    const groups = groupMetadata.groupingFunction(items);

    const titleTransform = groupMetadata.titleTransform || ((title: string) => title);
    return this.contextProvider.pipe(
      map((context) => {
        groups.forEach((g) => {
          g.title = titleTransform(g.title, context);
        });
        return groups;
      })
    );
  }

  private getGroupMetadata(group: string): GrouperMetadata<T, C> {
    const config = this.metadata.get(group);

    if (!config) {
      throw Error(`Missing metadata for group ${group}`);
    }

    return config;
  }
}

/** Utility function that creates a group based on the value of the item's property. */
export function getGroupByValue<T>(items: T[], property: string): Group<T>[] {
  const valueMap: Map<string, T[]> = new Map();

  items.forEach((item: any) => {
    const value = item[property];
    if (!valueMap.has(value)) {
      valueMap.set(value, []);
    }

    valueMap.get(value).push(item);
  });

  return getGroupsFromMap(valueMap);
}

/** Utility function that creates a group based on the list of values of the item's property. */
export function getGroupByListValues<T>(items: T[], key: string): Group<T>[] {
  const valueMap: Map<string, T[]> = new Map();
  items.forEach((item: any) => {
    let values: any[] = item[key];
    if (!values || !values.length) {
      values = [null];
    }
    values.forEach((value: any) => {
      if (!valueMap.get(value)) {
        valueMap.set(value, []);
      }
      valueMap.get(value).push(item);
    });
  });

  return getGroupsFromMap(valueMap);
}

/** Utility function that transforms a map of groups into a list. */
export function getGroupsFromMap<T>(groupsMap: Map<string, T[]>): Group<T>[] {
  const groups: Group<T>[] = [];
  groupsMap.forEach((items, title) => {
    title = `${title}`; // TItle should always be a string, even if the ID is something else.
    groups.push({ id: title, title, items });
  });

  return groups;
}

