(registry: MatIconRegistry, domSanitizer: DomSanitizer) => {
    Object.keys(svgIcons).map(name => {
        const path = svgIcons[name];
        registry.addSvgIcon(name, domSanitizer.bypassSecurityTrustResourceUrl(path));
    });
}

