@Pipe({
  name: 'bungieDateTime'
})
export class BungieDateTimePipe extends DatePipe implements PipeTransform {
  constructor(@Inject(LOCALE_ID) locale: string) {
    super(locale);
  }

  override transform(date: Date): any {
    return super.transform(date, 'yyyy-MM-dd h:mm a');
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { MembershipTypes } from '@destiny/data/models/enums';

@Pipe({
  name: 'memberType'
})
export class MemberTypePipe implements PipeTransform {
  transform(value: number): string {
    switch (value) {
      case MembershipTypes.Xbox:
        return 'xbox';
      case MembershipTypes.Psn:
        return 'playstation';
      case MembershipTypes.Steam:
        return 'steam';
      case MembershipTypes.Stadia:
        return 'stadia';
      default:
        return '';
    }
  }
}

