export type TextEquality = 'contains' | 'is' | 'notContains' | 'notIs';
export type NumberEquality = 'greaterThan' | 'lessThan' | 'equalTo';
export type DateEquality = 'before' | 'after' | 'on';
export type StateEquality = 'is' | 'notIs';

export interface TextFilter {
  id: string;
  type: 'text';
  value: string;
  equality: TextEquality;
}

export interface NumberFilter {
  id: string;
  type: 'number';
  value: number;
  equality: NumberEquality;
}

export interface DateFilter {
  id: string;
  type: 'date';
  date: string;
  equality: DateEquality;
}

export interface StateFilter {
  id: string;
  type: 'state';
  state: string;
  equality: StateEquality;
}

export type Filter = TextFilter | NumberFilter | DateFilter | StateFilter;

export type FilterType = 'text' | 'number' | 'date' | 'state';

import {combineLatest, EMPTY, Observable, ReplaySubject} from 'rxjs';
import {map, startWith, take} from 'rxjs/operators';
import {
  DateFilter,
  Filter,
  FilterType,
  NumberFilter,
  StateFilter,
  TextFilter
} from './filterer-types';

export type TextFilterAutocomplete<T = any, C = any> = (items: T[], c: C) => string[];

export interface TextFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'text';
  matcher: (item: T, q: TextFilter, c: C) => boolean;
  autocomplete?: TextFilterAutocomplete<T, C>;
}

export interface NumberFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'number';
  matcher: (item: T, q: NumberFilter, c: C) => boolean;
}

export interface DateFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'date';
  matcher: (item: T, q: DateFilter, c: C) => boolean;
}

export interface StateFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'state';
  matcher: (item: T, q: StateFilter, c: C) => boolean;
  states: string[];
}

export type FiltererMetadata<T = any, C = any> = TextFiltererMetadata<T, C> |
  NumberFiltererMetadata<T, C> | DateFiltererMetadata<T, C> | StateFiltererMetadata<T, C>;

export interface FiltererState {
  filters: Filter[];
  search: string;
}

export interface FilterOption {
  id: string;
  label: string;
  type: FilterType;
}

export type FiltererContextProvider<M> = Observable<M>;

export interface FiltererOptions<T, C> {
  metadata?: Map<string, FiltererMetadata<T, C>>;
  contextProvider?: FiltererContextProvider<C>;
  initialState?: FiltererState;
  tokenizeItem?: (item: T) => string;
}

/** Default and naive tokenize function that combines the item's property values into a string. */
const DEFAULT_TOKENIZE_ITEM =
  (data: any) => {
    return Object.keys(data)
      .reduce(
        (currentTerm: string, key: string) => {
          return currentTerm + (data as {[key: string]: any})[key] + '';
        },
        '')
      .toLowerCase();
  };

export class Filterer<T = any, C = any> {
  private readonly metadata: Map<string, FiltererMetadata<T, C>>;

  private contextProvider: Observable<C>;

  private readonly tokenizeItem: (item: T) => string;

  state = new ReplaySubject<FiltererState>(1);

  constructor(options: FiltererOptions<T, C> = {}) {
    this.metadata = options.metadata || new Map();
    this.state.next(options.initialState || {filters: [], search: ''});
    this.contextProvider = options.contextProvider || EMPTY.pipe(startWith(null));
    this.tokenizeItem = options.tokenizeItem || DEFAULT_TOKENIZE_ITEM;
  }

  /** Gets a stream that returns the items and updates whenever the filters or search changes. */
  filter(): (items: Observable<T[]>) => Observable<T[]> {
    return (items$: Observable<T[]>) => {
      return combineLatest(items$, this.state, this.contextProvider).pipe(
        map(([items, state, contextProvider]) => {
          const filters = state.filters;
          const search = state.search;
          const filteredItems = filterItems(items, filters, contextProvider, this.metadata);
          return searchItems(filteredItems, search, this.tokenizeItem);
        }));
    };
  }

  setState(state: FiltererState) {
    this.state.next({...state});
  }

  isEquivalent(otherState?: FiltererState): Observable<boolean> {
    return this.state.pipe(map(state => {
      if (!otherState) {
        return false;
      }

      const filtersEquivalent =
        JSON.stringify(state.filters.sort()) === JSON.stringify(otherState.filters.sort());
      const searchEquivalent = state.search === otherState.search;

      return filtersEquivalent && searchEquivalent;
    }));
  }

  getFilterOptions(): FilterOption[] {
    const filterOptions: FilterOption[] = [];
    this.metadata.forEach((value, key) => {
      filterOptions.push({id: key, label: value.label, type: value.type});
    });
    return filterOptions;
  }

  getStateFilterOptions(id: string): string[] {
    const filtererMetadata = this.metadata.get(id);

    if (filtererMetadata.type !== 'state') {
      throw Error(`Cannot get state options for filters with type ${filtererMetadata.type}`);
    }

    return filtererMetadata.states;
  }

  transformTextFilterOptions(id: string): (items: Observable<T[]>) => Observable<string[]> {
    const filtererMetadata = this.metadata.get(id);

    if (filtererMetadata.type !== 'text') {
      throw Error(`Cannot get text options for filters with type ${filtererMetadata.type}`);
    }

    return (items$: Observable<T[]>) => {
      return combineLatest(items$, this.contextProvider).pipe(map(([items, context]) => {
        if (!filtererMetadata.autocomplete) {
          return [];
        }
        return filtererMetadata.autocomplete(items, context);
      }));
    };
  }

  add(filter: Filter) {
    this.state.pipe(take(1)).subscribe(state => {
      const filters = state.filters.slice();
      filters.push(filter);
      this.setState({...state, filters});
    });
  }

  remove(filter: Filter) {
    this.state.pipe(take(1)).subscribe(state => {
      const filters = state.filters.slice();
      const index = state.filters.indexOf(filter);

      if (index !== -1) {
        filters.splice(index, 1);
        this.setState({...state, filters});
      }
    });
  }
}

/** Utility function to filter the items. May be used to synchronously filter items. */
export function filterItems<T, M>(
  items: T[], filters: Filter[] = [], context: M, metadata: Map<string, FiltererMetadata<T, M>>) {
  return items.filter(item => {
    return filters.every(filter => {
      const filterMetadata = metadata.get(filter.id);
      if (!filterMetadata || !filterMetadata.matcher) {
        throw Error('Missing matcher for ' + filter.id);
      }

      switch (filterMetadata.type) {
        case 'text':
          return filterMetadata.matcher(item, filter as TextFilter, context);
        case 'date':
          return filterMetadata.matcher(item, filter as DateFilter, context);
        case 'number':
          return filterMetadata.matcher(item, filter as NumberFilter, context);
        case 'state':
          return filterMetadata.matcher(item, filter as StateFilter, context);
      }
    });
  });
}

export function searchItems<T>(items: T[], search: string, tokenizeItem: (item: T) => string): T[] {
  return !search ? items : items.filter(item => {
    const tokens = search.split(' OR ');
    return tokens.some(token => {
      return tokenizeItem(item).indexOf(token.toLowerCase()) !== -1;
    });
  });
}

import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, Input, OnInit } from '@angular/core';
import { BehaviorSubject, combineLatest, map, Observable, shareReplay } from 'rxjs';

import { MatPaginatorModule, PageEvent } from '@angular/material/paginator';
import { MatTableModule } from '@angular/material/table';
import { MatDividerModule } from '@angular/material/divider';
import { RenderedViewComponent } from '../rendered-view/rendered-view.component';
import { DisplayOptionsComponent } from '../display-options/display-options.component';

import { RenderedView, Viewer, ViewLabel } from '../../data/viewer';
import { Exporter } from '../../data/exporter';
import { DataSource } from '../../data/data-source';
import { Filterer } from '../../data/filterer';
import { Grouper } from '../../data/grouper';
import { Sorter } from '../../data/sorter';
import { RenderedViewModule } from '../rendered-view/rendered-view.module';

export interface Item {
  id: string;
  assignees: string[];
  body: string;
  title: string;
  comments: number;
  labels: string[];
  number: number;
  state: string;
  reporter: string;
  created: string;
  closed: string;
  updated: string;
  // reactions: Reactions;
  pr: boolean;
  url: string;
  //  statuses: ItemStatus[];
  dbAdded?: string;
  dbModified?: string;
}

interface TablePage {
  index: number;
  size: number;
}

@Component({
  selector: 'lib-table-view',
  templateUrl: './table-view.component.html',
  imports: [
    CommonModule,
    MatTableModule,
    RenderedViewModule,
    MatPaginatorModule,
    MatDividerModule,
    DisplayOptionsComponent
  ],
  standalone: true,
  styleUrls: ['./table-view.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class TableViewComponent implements OnInit {
  displayedColumns: Observable<string[]>;

  views: ViewLabel[];

  renderedHtml: Observable<Map<Item, Map<string, Observable<RenderedView>>>>;

  @Input() filterer: Filterer;

  @Input() viewer: Viewer;

  @Input() grouper: Grouper;

  @Input() sorter: Sorter;

  @Input() dataSource: DataSource;

  @Input() exporter: Exporter;

  @Input() loading: boolean;

  itemCount: Observable<number>;

  page: BehaviorSubject<TablePage> = new BehaviorSubject({ size: 100, index: 0 });

  renderedData: Observable<Item[]>;
  constructor() {}

  ngOnInit(): void {
    // TODO: Cannot be in ngOnInit since the inputs may change
    const curatedData = this.dataSource.data.pipe(this.filterer.filter(), this.sorter.sort());
    this.renderedData = combineLatest([curatedData, this.page]).pipe(
      map(([data, page]) => data.slice(page.index * page.size, page.index * page.size + page.size))
    );
    this.itemCount = curatedData.pipe(map((d) => d.length));

    this.views = this.viewer.getViews();
    this.displayedColumns = this.viewer.state.pipe(
      map((state) => {
        return this.views.map((v) => v.id).filter((v) => state.views.indexOf(v) !== -1);
      })
    );

    this.renderedHtml = this.renderedData.pipe(
      map((items) => {
        const renderedHtml = new Map<Item, Map<string, Observable<RenderedView>>>();
        items.forEach((item) => {
          const itemRenderedViews = new Map<string, Observable<RenderedView>>();
          this.views.forEach((view) => itemRenderedViews.set(view.id, this.viewer.getRenderedView(item, view.id)));
          renderedHtml.set(item, itemRenderedViews);
        });
        return renderedHtml;
      }),
      shareReplay(1)
    );
  }

  setPage(event: PageEvent) {
    this.page.next({ index: event.pageIndex, size: event.pageSize });
  }
  export() {
    this.exporter.exportData('', this.renderedData);
  }
}

export type TextEquality = 'contains' | 'is' | 'notContains' | 'notIs';
export type NumberEquality = 'greaterThan' | 'lessThan' | 'equalTo';
export type DateEquality = 'before' | 'after' | 'on';
export type StateEquality = 'is' | 'notIs';

export interface TextFilter {
  id: string;
  type: 'text';
  value: string;
  equality: TextEquality;
}

export interface NumberFilter {
  id: string;
  type: 'number';
  value: number;
  equality: NumberEquality;
}

export interface DateFilter {
  id: string;
  type: 'date';
  date: string;
  equality: DateEquality;
}

export interface StateFilter {
  id: string;
  type: 'state';
  state: string;
  equality: StateEquality;
}

export type Filter = TextFilter | NumberFilter | DateFilter | StateFilter;

export type FilterType = 'text' | 'number' | 'date' | 'state';

import {combineLatest, EMPTY, Observable, ReplaySubject} from 'rxjs';
import {map, startWith, take} from 'rxjs/operators';
import {
  DateFilter,
  Filter,
  FilterType,
  NumberFilter,
  StateFilter,
  TextFilter
} from './filterer-types';

export type TextFilterAutocomplete<T = any, C = any> = (items: T[], c: C) => string[];

export interface TextFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'text';
  matcher: (item: T, q: TextFilter, c: C) => boolean;
  autocomplete?: TextFilterAutocomplete<T, C>;
}

export interface NumberFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'number';
  matcher: (item: T, q: NumberFilter, c: C) => boolean;
}

export interface DateFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'date';
  matcher: (item: T, q: DateFilter, c: C) => boolean;
}

export interface StateFiltererMetadata<T = any, C = any> {
  label: string;
  type: 'state';
  matcher: (item: T, q: StateFilter, c: C) => boolean;
  states: string[];
}

export type FiltererMetadata<T = any, C = any> = TextFiltererMetadata<T, C> |
  NumberFiltererMetadata<T, C> | DateFiltererMetadata<T, C> | StateFiltererMetadata<T, C>;

export interface FiltererState {
  filters: Filter[];
  search: string;
}

export interface FilterOption {
  id: string;
  label: string;
  type: FilterType;
}

export type FiltererContextProvider<M> = Observable<M>;

export interface FiltererOptions<T, C> {
  metadata?: Map<string, FiltererMetadata<T, C>>;
  contextProvider?: FiltererContextProvider<C>;
  initialState?: FiltererState;
  tokenizeItem?: (item: T) => string;
}

/** Default and naive tokenize function that combines the item's property values into a string. */
const DEFAULT_TOKENIZE_ITEM =
  (data: any) => {
    return Object.keys(data)
      .reduce(
        (currentTerm: string, key: string) => {
          return currentTerm + (data as {[key: string]: any})[key] + '';
        },
        '')
      .toLowerCase();
  };

export class Filterer<T = any, C = any> {
  private readonly metadata: Map<string, FiltererMetadata<T, C>>;

  private contextProvider: Observable<C>;

  private readonly tokenizeItem: (item: T) => string;

  state = new ReplaySubject<FiltererState>(1);

  constructor(options: FiltererOptions<T, C> = {}) {
    this.metadata = options.metadata || new Map();
    this.state.next(options.initialState || {filters: [], search: ''});
    this.contextProvider = options.contextProvider || EMPTY.pipe(startWith(null));
    this.tokenizeItem = options.tokenizeItem || DEFAULT_TOKENIZE_ITEM;
  }

  /** Gets a stream that returns the items and updates whenever the filters or search changes. */
  filter(): (items: Observable<T[]>) => Observable<T[]> {
    return (items$: Observable<T[]>) => {
      return combineLatest(items$, this.state, this.contextProvider).pipe(
        map(([items, state, contextProvider]) => {
          const filters = state.filters;
          const search = state.search;
          const filteredItems = filterItems(items, filters, contextProvider, this.metadata);
          return searchItems(filteredItems, search, this.tokenizeItem);
        }));
    };
  }

  setState(state: FiltererState) {
    this.state.next({...state});
  }

  isEquivalent(otherState?: FiltererState): Observable<boolean> {
    return this.state.pipe(map(state => {
      if (!otherState) {
        return false;
      }

      const filtersEquivalent =
        JSON.stringify(state.filters.sort()) === JSON.stringify(otherState.filters.sort());
      const searchEquivalent = state.search === otherState.search;

      return filtersEquivalent && searchEquivalent;
    }));
  }

  getFilterOptions(): FilterOption[] {
    const filterOptions: FilterOption[] = [];
    this.metadata.forEach((value, key) => {
      filterOptions.push({id: key, label: value.label, type: value.type});
    });
    return filterOptions;
  }

  getStateFilterOptions(id: string): string[] {
    const filtererMetadata = this.metadata.get(id);

    if (filtererMetadata.type !== 'state') {
      throw Error(`Cannot get state options for filters with type ${filtererMetadata.type}`);
    }

    return filtererMetadata.states;
  }

  transformTextFilterOptions(id: string): (items: Observable<T[]>) => Observable<string[]> {
    const filtererMetadata = this.metadata.get(id);

    if (filtererMetadata.type !== 'text') {
      throw Error(`Cannot get text options for filters with type ${filtererMetadata.type}`);
    }

    return (items$: Observable<T[]>) => {
      return combineLatest(items$, this.contextProvider).pipe(map(([items, context]) => {
        if (!filtererMetadata.autocomplete) {
          return [];
        }
        return filtererMetadata.autocomplete(items, context);
      }));
    };
  }

  add(filter: Filter) {
    this.state.pipe(take(1)).subscribe(state => {
      const filters = state.filters.slice();
      filters.push(filter);
      this.setState({...state, filters});
    });
  }

  remove(filter: Filter) {
    this.state.pipe(take(1)).subscribe(state => {
      const filters = state.filters.slice();
      const index = state.filters.indexOf(filter);

      if (index !== -1) {
        filters.splice(index, 1);
        this.setState({...state, filters});
      }
    });
  }
}

/** Utility function to filter the items. May be used to synchronously filter items. */
export function filterItems<T, M>(
  items: T[], filters: Filter[] = [], context: M, metadata: Map<string, FiltererMetadata<T, M>>) {
  return items.filter(item => {
    return filters.every(filter => {
      const filterMetadata = metadata.get(filter.id);
      if (!filterMetadata || !filterMetadata.matcher) {
        throw Error('Missing matcher for ' + filter.id);
      }

      switch (filterMetadata.type) {
        case 'text':
          return filterMetadata.matcher(item, filter as TextFilter, context);
        case 'date':
          return filterMetadata.matcher(item, filter as DateFilter, context);
        case 'number':
          return filterMetadata.matcher(item, filter as NumberFilter, context);
        case 'state':
          return filterMetadata.matcher(item, filter as StateFilter, context);
      }
    });
  });
}

export function searchItems<T>(items: T[], search: string, tokenizeItem: (item: T) => string): T[] {
  return !search ? items : items.filter(item => {
    const tokens = search.split(' OR ');
    return tokens.some(token => {
      return tokenizeItem(item).indexOf(token.toLowerCase()) !== -1;
    });
  });
}

