import { Injectable } from '@angular/core';
import { Store } from '@ngrx/store';
import { selectEnabledClanIds, selectEnabledClans, selectLastProfileUpdate } from '@core/store/clans';
import { forkJoin, from, map, mergeMap, switchMap, tap, toArray, combineLatest, distinctUntilChanged, Observable } from 'rxjs';
// import {} from '@destiny/data/';
import { ClanMembersService } from '@destiny/data/clan/clan-members';
import { ProfileService } from 'projects/data/src/lib/clan/profiles/profile.service';
import { MemberProfile } from '@destiny/data/models';
import { GroupsV2GroupMember } from 'bungie-api-angular';

export interface ClanMemberProfile {
  clanId: string;
  member: GroupsV2GroupMember;
  profile: MemberProfile;
}

@Injectable()
export class ClansRosterService {
  activeClansId$ = this.store.select(selectEnabledClanIds);

  activeClanUpdateDates$ = this.activeClansId$.pipe(
    switchMap((clanIds) => {
      const arraySelectors = clanIds.map((clanId) => {
        return this.store.select(selectLastProfileUpdate(clanId));
      });

      return combineLatest(arraySelectors);
      // return from(clanIds).pipe(
      //   switchMap((clanId) => {
      //     console.log('', clanId);
      //     return this.store.select(selectLastProfileUpdate(clanId));
      //   })

      //   // tap((x) => {
      //   //   console.log('tapping1', x);
      //   // })
      // );
    })
  );

  clanMembers$ = this.activeClansId$.pipe(
    switchMap((activeClanIds) => {
      console.log('active clans');
      return from(activeClanIds).pipe(
        mergeMap((clanId) => {
          console.log('mmerge map');
          return this.memberService.getClanMembersSerialized(clanId).pipe(
            map((result) => {
              return { clanId, members: result };
            })
          );
        }),
        toArray()
      );
    }),
    tap((x) => console.log(x))
  );

  clanProfiles$: Observable<ClanMemberProfile[]> = this.clanMembers$.pipe(
    switchMap((clansAndMembers) => {
      return from(clansAndMembers).pipe(
        mergeMap((clanAndMembers) => {
          return this.profileService
            .getSerializedProfiles<MemberProfile>(clanAndMembers.clanId, clanAndMembers.members, [], [])
            .pipe(
              map((result: MemberProfile) => {
                return {
                  clanId: clanAndMembers.clanId,
                  member: clanAndMembers.members.find(
                    (x) => x.destinyUserInfo?.membershipId == result.profile.data.userInfo?.membershipId
                  ),
                  profile: result
                };
              })
            );
        }),
        toArray()
      );
    })
  );

  // Whenever activeClanUpdateDates gets updated. Run this observable.
  activeClanPeople$ = this.activeClanUpdateDates$.pipe(
    map((x) => {
      return (
        combineLatest([this.activeClansId$, this.activeClanUpdateDates$]),
        map(([clans, clanDates]) => {
          return clans;
        })
      );
    })
  );

  constructor(
    private store: Store,
    private memberService: ClanMembersService,
    private profileService: ProfileService
  ) {}
}

import { Injectable } from '@angular/core';
import { Store } from '@ngrx/store';
import { mergeMap, map, catchError, toArray, switchMap, tap, take, filter } from 'rxjs/operators';
import { ClanConfig, selectEnabledClans, updateClanProfileSync } from '../store/clans';
import { ClanMembersService } from '@destiny/data/clan/clan-members';
import { from, Observable, of } from 'rxjs';
import { GroupsV2GroupMember } from 'bungie-api-angular';
import { ProfileWorkerService } from '../../workers/profile-worker.service';
import { nowPlusMinutes } from 'projects/data/src/lib/utility/date-utils';
import { AppConfig } from '@core/config/app-config';

interface ClanConfigMembers {
  clanConfig: ClanConfig;
  members: GroupsV2GroupMember[];
}

@Injectable({
  providedIn: 'root'
})
export class ClanUpdaterService {
  activeClanIds$ = this.store.select(selectEnabledClans);

  constructor(
    private store: Store,
    private memberService: ClanMembersService,
    private profileWorkerService: ProfileWorkerService,
    private appConfig: AppConfig
  ) {}

  update() {
    return this.activeClanIds$.pipe(
      // tap((x) => console.log('starting')),
      switchMap((activeClans) => this.memberUpdate(activeClans)),
      //tap((x) => console.log('Member Update Complete', x)),
      switchMap((clans) => this.profilesUpdate(clans)),
      // tap((x) => console.log('complete', x))
    );
  }

  memberUpdate(activeClans) {
    return from(activeClans).pipe(
      mergeMap((clanConfig: ClanConfig) => {
        //console.log('merge map', clanConfig.clanId);
        return this.memberService.getClanMembersSerialized(clanConfig.clanId).pipe(
          map((members) => ({
            members,
            clanConfig
          }))
        );
      }, 1),
      toArray()
    );
  }

  profilesUpdate(clans: ClanConfigMembers[]): Observable<ClanConfigMembers[]> {
    return from(clans).pipe(
      mergeMap((x) => {
        return this.profileUpdate(x).pipe(map((cm) => x));
        //}

        //
        //return of(x);
      }, 1),
      toArray(),
   ///   tap((x) => console.log('toarray 2', x))
    );
  }

  profileUpdate(clan: ClanConfigMembers): Observable<ClanConfigMembers> {
    const lastUpdate = new Date(clan.clanConfig.profileUpdate || '1/1/1900');
    const staleDate = nowPlusMinutes(-this.appConfig.constants.PROFILE_UPDATING_EXP_MINUTES);

    if (staleDate > lastUpdate) {
      //      console.log(`Updating ${clan.clanConfig.clanId}`);
      //if (true === true) {
      // this.store.dispatch(
      //   addNotification({ notification: { id: 'memberProfile', title: 'Updating Profiles', data: { progress: 0 } } })
      // );
      const progress = (progressCount) => {
        // this.store.dispatch(
        //   updateNotification({
        //     notification: {
        //       id: 'memberProfile',
        //       title: 'Updating Profiles',
        //       data: { progress: progressCount }
        //     }
        //   })
        // );
        //       console.log('progress', progressCount);
      };
      this.profileWorkerService.loadProfiles(clan.clanConfig.clanId, clan.members, progress);
      return this.profileWorkerService.members.pipe(
        filter((x) => x.length > 0),
        take(1),
        map((x) => {
          // this.store.dispatch(memberProfileActions.loadMemberProfiles({ memberProfiles: x }));
          // this.store.dispatch(
          //   removeNotification({ notification: { id: 'memberProfile', title: 'Updating Profiles', data: 'done' } })
          // );
          // return memberProfileActions.loadMemberProfileSuccess();
          //         console.log(`done ${clan.clanConfig.clanId}`, x);
          this.store.dispatch(updateClanProfileSync({ clanId: clan.clanConfig.clanId }));
          return clan;
        })
      );
    }
    //console.log(`Valid Cache ${clan.clanConfig.clanId}`);
    return of(clan);
  }
}

import { Injectable } from '@angular/core';

const APP_PREFIX = 'D2DASH-';

@Injectable({
  providedIn: 'root'
})
export class LocalStorageService {
  constructor() {}

  static loadInitialState() {
    return Object.keys(localStorage).reduce((state: any, storageKey) => {
      if (storageKey.includes(APP_PREFIX)) {
        const stateKeys = storageKey
          .replace(APP_PREFIX, '')
          //.toLowerCase()
          .split('.')
          .map((key) =>
            key
              .split('-')
              .map((token, index) => (index === 0 ? token : token.charAt(0).toUpperCase() + token.slice(1)))
              .join('')
          );
        let currentStateRef = state;
        stateKeys.forEach((key, index) => {
          if (index === stateKeys.length - 1) {
            currentStateRef[key] = JSON.parse(localStorage.getItem(storageKey));
            return;
          }
          currentStateRef[key] = currentStateRef[key] || {};
          currentStateRef = currentStateRef[key];
        });
      }
      return state;
    }, {});
  }

  setItem(key: string, value: any) {
    localStorage.setItem(`${APP_PREFIX}${key}`, JSON.stringify(value));
  }

  getItem(key: string) {
    return JSON.parse(localStorage.getItem(`${APP_PREFIX}${key}`));
  }

  removeItem(key: string) {
    localStorage.removeItem(`${APP_PREFIX}${key}`);
  }

  /** Tests that localStorage exists, can be written to, and read from. */
  testLocalStorage() {
    const testValue = 'testValue';
    const testKey = 'testKey';
    let retrievedValue: string;
    const errorMessage = 'localStorage did not return expected value';

    this.setItem(testKey, testValue);
    retrievedValue = this.getItem(testKey);
    this.removeItem(testKey);

    if (retrievedValue !== testValue) {
      throw new Error(errorMessage);
    }
  }
}

import { Injectable } from '@angular/core';
import { AppIndexedDb, StoreId } from '../db/app-indexed-db';

@Injectable({
  providedIn: 'root'
})
export class ManifestDatabaseService {

  private database: AppIndexedDb;

  getValues(repository: string): Promise<any[]> {
    return this.getDatabase(repository).getAllData('allData');
  }

  closeDatabase(repository: string) {
    return this.getDatabase(repository).close();
  }

  update(repository: string, type: StoreId, entities: any[]): Promise<void> {
    return this.getDatabase(repository).updateValues(entities, type);
  }

  remove(repository: string, type: StoreId, ids: string[]): Promise<void> {
    return this.getDatabase(repository).removeValues(ids, type);
  }

  removeAll(repository: string, type: StoreId) {
    return this.getDatabase(repository).removeAllValues(type);
  }

  private getDatabase(repository: string) {
    if (this.database && this.database.name === repository) {
      return this.database;
    }

    if (this.database) {
      this.database.close();
    }

    this.database = new AppIndexedDb(repository);
    return this.database;
  }
}

