import { openDB, deleteDB, IDBPDatabase } from 'idb';

export class BaseAppIndexedDb {
  name: string;
  dbVersion: number;
  storeIds: string[];

  private db: Promise<IDBPDatabase>;

  constructor(name: string, dbVersion: number, storeIds: string[], initializeValues: boolean = true) {
    this.dbVersion = dbVersion;
    this.storeIds = storeIds;

    if (initializeValues) {
      this.resetInitialValues();
    }
    this.name = name;
    this.openDb();
  }

  close() {
    return this.db.then((db) => db.close());
  }

  private resetInitialValues() {}

  getAllData(store: string) {
    return this.db.then((db) => {
      return db.transaction(store, 'readonly').objectStore(store).getAll();
    });
  }

  getById(store: string, id) {
    return this.db.then((db) => {
      return db.transaction(store, 'readonly').objectStore(store).get(id);
    });
  }

  removeData() {
   return this.db
      .then((db) => {
        this.resetInitialValues();
        db.close();
        return deleteDB(this.name);
      })
      .then(() => this.openDb());
  }

  purgeDatabase() {
    return this.close().then((db) => {
      return deleteDB(this.name);
    });
  }

  updateValues(values: any[], collectionId: string) {
    return this.db.then((db) => {
      const transaction = db.transaction(collectionId, 'readwrite');
      const store = transaction.objectStore(collectionId);
      values.forEach((v) => store.put(v));
      return transaction.done;
    });
  }

  removeValues(ids: string[], collectionId: string) {
    return this.db.then((db) => {
      const transaction = db.transaction(collectionId, 'readwrite');
      const store = transaction.objectStore(collectionId);
      ids.forEach((id) => store.delete(id));
      return transaction.done;
    });
  }

  removeAllValues(collectionId: string) {
    return this.db.then((db) => {
      const transaction = db.transaction(collectionId, 'readwrite');
      const store = transaction.objectStore(collectionId);
      return store.clear();
    });
  }

  private openDb() {
    const base = this;
    this.db = openDB(this.name, this.dbVersion, {
      upgrade(db, oldVersion, newVersion, transaction) {
        base.storeIds.forEach((collectionId) => {
          if (!db.objectStoreNames.contains(collectionId)) {
            const objectStore = db.createObjectStore(collectionId, {
              keyPath: 'id'
            });
          }
        });
      }
    });
  }
}

import { ClanDatabase } from './clan-database';
import { DBObject, StoreId } from '../db/clan-indexed-db';
import { isValidDate, nowPlusMinutes } from '../utility/date-utils';

export class BaseClanService {
  tableName;
  constructor(private clanDbBase: ClanDatabase, private tableNameBase: StoreId) {
    this.tableName = tableNameBase;
  }

  getDataFromCache(clanId: string, rowId: string): Promise<DBObject> {
    return this.clanDbBase.getById(clanId, this.tableNameBase, rowId);
  }

  getAllDataFromCache(clanId: string): Promise<DBObject[]> {
    return this.clanDbBase.getAll(clanId, this.tableNameBase);
  }

  isCacheValid(cachedData: DBObject, minuteExpiration: number, lastActivity?: Date) {
    if (cachedData && cachedData.createDate) {
      const cacheDate = cachedData.createDate;
      let expireDate;
      if (isValidDate(lastActivity)) {
        if (minuteExpiration === 0) {
          expireDate = lastActivity;
        } else {
          const minuteXP = nowPlusMinutes(-minuteExpiration);
          const lastActivityXP = lastActivity;
          expireDate = minuteXP > lastActivityXP ? lastActivityXP : minuteXP;
        }
      } else {
        expireDate = nowPlusMinutes(-minuteExpiration);
      }
      return cacheDate > expireDate;
    }
    return false;
  }

  updateDB(clanId: string, rowId: string, data: any) {
    this.clanDbBase.update(clanId, this.tableName, [
      {
        id: rowId,
        createDate: new Date(),
        data: data
      }
    ]);
  }
}

