import {combineLatest, EMPTY, Observable, ReplaySubject} from 'rxjs';
import {map, startWith} from 'rxjs/operators';

export interface SorterState {
  sort: string;
  reverse: boolean;
}

export type SortComparator<T, C> = (a: T, b: T, context: C) => number;

export interface SorterMetadata<T = any, C = any> {
  label: string;
  comparator: SortComparator<T, C>;
}

export type SorterContextProvider<C> = Observable<C>;

export interface SortLabel {
  id: string;
  label: string;
}

function sortItems<T, C>(
    items: T[], comparator: SortComparator<T, C>, reverse: boolean, context: C) {
  items.sort((a, b) => comparator(a, b, context));

  if (reverse) {
    items.reverse();
  }

  return items;
}

export interface SorterOptions<T, C> {
  metadata?: Map<string, SorterMetadata<T, C>>;
  contextProvider?: SorterContextProvider<C>;
  initialState?: SorterState;
}

export class Sorter<T = any, C = any> {
  private metadata: Map<string, SorterMetadata<T, C>>;

  private contextProvider: SorterContextProvider<C>;

  state = new ReplaySubject<SorterState>(1);

  constructor(options: SorterOptions<T, C> = {}) {
    this.metadata = options.metadata || new Map();
    this.state.next(options.initialState || {sort: this.getSorts()[0].id, reverse: false});
    this.contextProvider = options.contextProvider || EMPTY.pipe(startWith(null));
  }

  sort(): (items$: Observable<T[]>) => Observable<T[]> {
    return (items$: Observable<T[]>) => {
      return combineLatest([items$, this.state, this.contextProvider])
          .pipe(map(([items, state, context]) => {
            const sortMetadata = this.metadata.get(state.sort);
            if (!sortMetadata) {
              throw new Error(`No configuration set up for sort ${state.sort}`);
            }

            return sortItems(items, sortMetadata.comparator, state.reverse, context);
          }));
    };
  }

  getSorts(): SortLabel[] {
    const sorts: SortLabel[] = [];
    this.metadata.forEach((value, key) => sorts.push({id: key, label: value.label}));
    return sorts;
  }

  setState(state: SorterState) {
    this.state.next({...state});
  }

  isEquivalent(otherState?: SorterState): Observable<boolean> {
    return this.state.pipe(map(state => {
      if (!otherState) {
        return false;
      }
      return state.sort === otherState.sort && state.reverse === otherState.reverse;
    }));
  }
}

import { combineLatest, EMPTY, Observable, ReplaySubject } from 'rxjs';
import { map, startWith, take } from 'rxjs/operators';

export interface ViewerState {
  views: string[];
}

interface RenderedViewWithText {
  text: string;
  classList?: string;
  styles?: { [key in string]: string };
}
interface RenderedWithComponent {
  component: any;
  data: any;
  classList?: string;
  styles?: { [key in string]: string };
}

interface RenderedViewWithChildren {
  children: RenderedView[];
  classList?: string;
  styles?: { [key in string]: string };
}

export type RenderedView = RenderedViewWithText | RenderedViewWithChildren | RenderedWithComponent;

export interface ViewerMetadata<T = any, C = any> {
  label: string;
  render: (item: T, context: C) => RenderedView | null;
}

export interface ViewLabel {
  id: string;
  label: string;
}

export type ViewerContextProvider<T, C> = Observable<(item: T) => C>;

export interface ViewerOptions<T, C> {
  metadata?: Map<string, ViewerMetadata<T, C>>;
  contextProvider?: ViewerContextProvider<T, C>;
  initialState?: ViewerState;
}

/** The viewer carries information to render the items to the view. */
export class Viewer<T = any, C = any> {
  private metadata: Map<string, ViewerMetadata<T, C>>;

  private contextProvider: ViewerContextProvider<T, C>;

  state = new ReplaySubject<ViewerState>(1);

  constructor(options: ViewerOptions<T, C> = {}) {
    this.metadata = options.metadata || new Map();
    this.setState(options.initialState || { views: this.getViews().map((v) => v.id) });
    this.contextProvider = options.contextProvider || EMPTY.pipe(startWith(() => null));
  }

  getViews(): ViewLabel[] {
    const views: ViewLabel[] = [];
    this.metadata.forEach((value, key) => views.push({ id: key, label: value.label }));
    return views;
  }

  toggle(view: string) {
    this.state.pipe(take(1)).subscribe((state) => {
      const views = state.views;

      const newViews = [...views];
      const index = views.indexOf(view);
      if (index !== -1) {
        newViews.splice(index, 1);
      } else {
        newViews.push(view);
      }

      this.setState({ views: newViews });
    });
  }

  setState(state: ViewerState) {
    // Remove any state keys that are not valid
    const views = state.views.filter((view) => !!this.metadata.get(view));
    this.state.next({ views });
  }

  isEquivalent(otherState?: ViewerState): Observable<boolean> {
    return this.state.pipe(
      map((state) => {
        if (!otherState) {
          return false;
        }
        const thisViews = state.views.slice().sort();
        const otherViews = otherState.views.slice().sort();

        return thisViews.length === otherViews.length && thisViews.every((v, i) => otherViews[i] === v);
      })
    );
  }

  getRenderedViews(item: T): Observable<RenderedView[]> {
    return combineLatest(this.state, this.contextProvider).pipe(
      map(([state, context]) => {
        const views = state.views.map((v) => this.metadata.get(v));
        return views.map((view) => view.render(item, context(item)));
      })
    );
  }

  getRenderedView(item: T, view: string): Observable<RenderedView> {
    return this.contextProvider.pipe(
      map((context) => {
        return this.metadata.get(view).render(item, context(item));
      })
    );
  }
}

